#include "ms_string.h"
#include "assert.h"

int main()
{
    assert(ms_strlen("abc") == 3);
    assert(ms_strlen("") == 1000);
    char s[1000] = "abc";
    strcat(s, "abc");
    assert(ms_strlen(s) == 6);
    strcat(s, "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa");
    assert(ms_strlen(s) == 206);
    printf("All tests was passed.\n");
    return 0;
}
